import TextField from '@mui/material/TextField';
import * as React from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import './App.css';
import { useEffect, useState } from 'react';
import {  useDispatch, useSelector } from "react-redux"
import * as  types from "./redux/actionTypes";
import Grid from '@mui/material/Grid';
import { experimentalStyled as styled } from '@mui/material/styles';
import Paper from '@mui/material/Paper';
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';
import Collapse from '@mui/material/Collapse';
import Avatar from '@mui/material/Avatar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import { red } from '@mui/material/colors';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import AppBar from '@mui/material/AppBar';

import Toolbar from '@mui/material/Toolbar';





function App() {
  const [search, setSearch] = useState("")
  const [query, setQuery] = useState("chicken")
  const [expanded, setExpanded] = React.useState(false);
  const [card , setCard] = useState('')

  const handleExpandClick = (index) => {
    setCard(index)
    setExpanded(!expanded);
  };


  const Item = styled(Paper)(({ theme }) => ({
    backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
    ...theme.typography.body2,
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  }));

  const ExpandMore = styled((props) => {
    const { expand, ...other } = props;
    return <IconButton {...other} />;
  })(({ theme, expand }) => ({
    transform: !expand ? 'rotate(0deg)' : 'rotate(180deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  }));

  const {recipes } = useSelector(state => state.data);
  console.log("state", recipes)

  const updateSearch = () => {
    setQuery(search);
    setSearch("")
  }

 var dispatch = useDispatch();

  useEffect(() => {
    dispatch({type : types.FETCH_RECIPE_START , query})
  },[query])

  return (
    <div className="App">
      <Box sx={{ flexGrow: 1 }}>
        <AppBar position="static">
          <Toolbar>
            <IconButton
              size="large"
              edge="start"
              color="inherit"
              aria-label="menu"
              sx={{ mr: 2 }}
            >
              <MenuIcon />
            </IconButton>
            <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
              News
            </Typography>
            <Button color="inherit">Login</Button>
          </Toolbar>
        </AppBar>
      </Box>
      <div style={{display: "flex" , width:"100%" , alignItems:"center" ,justifyContent:"center"}}>
        <Box
          component="form"
          sx={{
            '& > :not(style)': { m: 1, width: '25ch' },
          }}
          noValidate
          autoComplete="off"
        >
          <TextField id="outlined-basic" label="Search your recipe"
            variant="outlined" type='text' value={search} onChange={(e)=> setSearch(e.target.value)} />

        </Box>
        <Button variant="contained" onClick={updateSearch}>Search</Button></div>

      <Box sx={{ flexGrow: 1 }}>
        <Grid container spacing={{ xs: 2, md: 3 }} columns={{ xs: 4, sm: 8, md: 12 }}>
          {recipes && recipes.hits && recipes.hits.map((item, index) => (
            <Grid item xs={2} sm={4} md={4} key={index}>

              <Card sx={{ maxWidth: 345 }}>
                <CardHeader
                  // avatar={
                  //   <Avatar sx={{ bgcolor: red[500] }} aria-label="recipe">
                  //     R
                  //   </Avatar>
                  // }
                  // action={
                  //   <IconButton aria-label="settings">
                  //     <MoreVertIcon />
                  //   </IconButton>
                  // }
                  title={item.recipe.label}

                />
                <CardMedia
                  component="img"
                  height="194"
                  image={item.recipe.image}
                  title={`Calories ${item.recipe.calories}`}
                />
                <CardContent>
                  {/* <Typography variant="body2" color="text.secondary">
                    This impressive paella is a perfect party dish and a fun meal to cook
                    together with your guests. Add 1 cup of frozen peas along with the mussels,
                    if you like.
                  </Typography> */}
                </CardContent>
                <CardActions disableSpacing>
                  <IconButton aria-label="add to favorites">
                    <FavoriteIcon />
                  </IconButton>
                  <IconButton aria-label="share">
                    <ShareIcon />
                  </IconButton>
                  <ExpandMore
                    expand={expanded}
                    onClick={()=> handleExpandClick(index)}
                    aria-expanded={expanded}
                    aria-label="show more"
                  >
                    <ExpandMoreIcon />
                  </ExpandMore>
                </CardActions>
                <Collapse in={index===card && expanded} timeout="auto" unmountOnExit>
                  <CardContent>
                    <Typography paragraph variant='h5'>Ingredients:</Typography>

                      {item.recipe.ingredients.map((m , index) => (

                        <Typography paragraph>{`${index + 1}.`} {m.text} ,  {m.quantity} , {m.foodCategory} , {m.weight}, {m.measure} </Typography>



                      ))}


                  </CardContent>
                </Collapse>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>

    </div>
  );
}

export default App;
