import * as  types from "./actionTypes";

const initialState = {
    
    error: null,
    isLoading: false,
    recipes: []
};



export  const recipeReducer = (state = initialState, action) => {
    const { type, payload } = action;
    switch (action.types) {
        case types.FETCH_RECIPE_START: 
            return {
                ...state,
                isLoading: true,
                
            }
        case types.FETCH_RECIPE_SUCCESS:
            return {
                ...state,
                isLoading: false,
                recipes : action.data,
            }
        case types.FETCH_RECIPE_FAIL:
            return {
                ...state,
                isLoading: false,
                error: payload

            }
        default:
            return state;
    }
}
