import TextField from '@mui/material/TextField';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import './App.css';
import { useEffect, useState } from 'react';
import { UseSelector, useDispatch, useSelector } from "react-redux"
import * as  types from "./redux/actionTypes";
import { recipeReducer } from './redux/reducer';

function App() {
  const [search, setSearch] = useState("")
  const [query, setQuery] = useState("chicken")


  const {recipes} = useSelector(state => state.data);
  console.log("state", recipes)

  const updateSearch = () => {
    setQuery(search);
    setSearch("")
  }

  let dispatch = useDispatch();

  useEffect(() => {
    dispatch({type : types.FETCH_RECIPE_START , payload : query})
  },[query])

  return (
    <div className="App">
      <div style={{display: "flex" , width:"100%" , alignItems:"center" ,justifyContent:"center"}}>
        <Box
          component="form"
          sx={{
            '& > :not(style)': { m: 1, width: '25ch' },
          }}
          noValidate
          autoComplete="off"
        >
          <TextField id="outlined-basic" label="Search your recipe"
            variant="outlined" type='text' value={search} onChange={(e)=> setSearch(e.target.value)} />

        </Box>
        <Button variant="contained" onClick={updateSearch}>Search</Button></div>
      {recipes && recipes.hits && recipes.hits.map((item) => (
        <h4>item</h4>
      ))}

    </div>
  );
}

export default App;
